package com.example.amazonx.model;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import javax.persistence.*;@Entity@Table(name = "orders")@Setter@Getter@NoArgsConstructorpublic class Order {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private long id;    private String upsUserId;    @OneToOne    @JoinColumn(name = "product_id")    private Product product;    private int quantity;    private int whnum;    private int destX;    private int destY;    private String status;    private int truckid = -1;    @ManyToOne    @JoinColumn(name = "username")    private User user;    public Order(String upsUserId            , Product product            , int quantity            , int whnum            , int destX            , int destY            , User user    ) {        this.upsUserId = upsUserId;        this.product = product;        this.quantity = quantity;        this.whnum = whnum;        this.destX = destX;        this.destY = destY;        this.status = "Purchasing";        this.user = user;    }}